mapper.select:
SELECT dad.id, dad.project_id, dad.task_name, da. NAME AS appName, dad.app_id, dad.trigger_version, dad.env_id, de. NAME AS envName, dad.value_id, dad.last_update_date, dad.object_version_number, dad.instance_id, dad.is_enabled FROM devops_auto_deploy dad JOIN devops_application da ON dad.app_id = da.id JOIN devops_env de ON dad.env_id = de.id
JOIN devops_env_user_permission deup ON ( deup.iam_user_id = #{userId} AND deup.is_permitted = 1 AND dad.env_id = deup.env_id )
where dad.project_id = #{projectId}
AND dad.app_id = #{appId}
AND dad.env_id = #{envId}
ORDER BY dad.is_enabled DESC, dad.id DESC

sql.if:
AND (dad.task_name LIKE CONCAT(CONCAT('%', #{param, jdbcType=VARCHAR}),'%')
OR dad.trigger_version LIKE CONCAT(CONCAT('%', #{param, jdbcType=VARCHAR}),'%')
)

mapper.update:
UPDATE devops_auto_deploy dad
SET dad.instance_id = NULL
WHERE dad.instance_id = #{instanceId}

mapper.select:
SELECT dad.id, dad.project_id, dad.task_name, dad.app_id, dad.trigger_version, dad.env_id, dad.value_id, dad.instance_id, dad.is_enabled FROM devops_auto_deploy dad JOIN devops_env de ON dad.env_id = de.id
JOIN devops_env_user_permission deup ON ( deup.iam_user_id = #{userId} AND deup.is_permitted = 1 AND dad.env_id = deup.env_id )
WHERE dad.project_id = #{projectId}
